mapper.select:
SELECT AA.CASE_SEQ ,AA.SERVICE_CD ,AB.SERVICE_NM ,AA.CASE_SJ ,AA.CASE_CN ,AA.REG_NM ,AA.FILE_SEQ ,AA.HASHTAG ,DATE_FORMAT(AA.RGS_DT, '%Y-%m-%d') AS RGS_DT FROM TB_CASE AA LEFT OUTER JOIN TB_CATEGORY AB ON AA.SERVICE_CD = AB.SERVICE_CD WHERE 1 = 1
AND AA.CASE_SJ LIKE CONCAT('%', #{caseSj},'%')
AND AA.HASHTAG LIKE CONCAT('%', #{hashtag},'%')
AND AA.SERVICE_CD = #{serviceCd}
ORDER BY AA.CASE_SEQ DESC LIMIT #{limitStartNum} , #{limitViewRowCnt}

mapper.update:
UPDATE 	TB_CASE
SET
		CASE_SJ		= #{caseSj}
		,CASE_CN	= #{caseCn}
		,REG_NM		= #{regNm}
		,FILE_SEQ	= #{fileSeq}
		,HASHTAG	= #{hashtag}
		,SERVICE_CD	= #{serviceCd}
WHERE 	CASE_SEQ 	= #{caseSeq}
