mapper.select:
SELECT COUNT(*) AS CNT
FROM TB_PRODUCT P
INNER JOIN TB_CATEGORY C ON P.SERVICE_CD = C.SERVICE_CD
WHERE 1=1
  AND P.DEL_YN = 'N'
  AND C.DEL_YN = 'N'



            AND P.PRODUCT_NM LIKE '%' || #{searchWord} || '%'


            AND P.PRODUCT_NO = #{searchWord}


            AND C.SERVICE_NM LIKE '%' || #{searchWord} || '%'


            AND (P.PRODUCT_NM LIKE '%' || #{searchWord} || '%'
                 OR C.SERVICE_NM LIKE '%' || #{searchWord} || '%')




    AND P.SERVICE_CD = #{serviceCd}


    AND P.DISPLAY_YN = #{displayYn}


    AND P.SALE_YN = #{saleYn}


    AND P.AREA_TYPE = #{areaType}

SELECT P.PRODUCT_NO, P.SERVICE_CD, C.SERVICE_NM, P.PRODUCT_NM, P.PRODUCT_DESC, P.AREA_TYPE, P.ORIGINAL_PRICE, P.SALE_PRICE, P.DISCOUNT_RATE, P.SERVICE_TIME, P.SERVICE_INCLUDES, P.DISPLAY_ORDER, P.DISPLAY_YN, P.SALE_YN, P.REG_USER_ID, DATE_FORMAT(P.REG_DT, '%Y-%m-%d %H:%i') AS REG_DT, P.MOD_USER_ID, DATE_FORMAT(P.MOD_DT, '%Y-%m-%d %H:%i') AS MOD_DT FROM TB_PRODUCT P INNER JOIN TB_CATEGORY C ON P.SERVICE_CD = C.SERVICE_CD WHERE 1=1 AND P.DEL_YN = 'N' AND C.DEL_YN = 'N'
AND P.PRODUCT_NM LIKE CONCAT('%', #{searchWord}, '%')
AND P.PRODUCT_NO = #{searchWord}
AND C.SERVICE_NM LIKE CONCAT('%', #{searchWord}, '%')
AND (P.PRODUCT_NM LIKE CONCAT('%', #{searchWord}, '%') OR C.SERVICE_NM LIKE CONCAT('%', #{searchWord}, '%'))
AND P.SERVICE_CD = #{serviceCd}
AND P.DISPLAY_YN = #{displayYn}
AND P.SALE_YN = #{saleYn}
AND P.AREA_TYPE = #{areaType}
ORDER BY P.DISPLAY_ORDER ASC, P.PRODUCT_NO DESC
LIMIT #{rowLimit} OFFSET #{rowStrt}

SELECT P.PRODUCT_NO, P.SERVICE_CD, C.SERVICE_NM, C.SERVICE_ORDER, P.PRODUCT_NM, P.PRODUCT_DESC, P.AREA_TYPE, P.ORIGINAL_PRICE, P.SALE_PRICE, P.DISCOUNT_RATE, P.SERVICE_TIME, P.SERVICE_INCLUDES, P.DISPLAY_ORDER, P.DISPLAY_YN, P.SALE_YN, DATE_FORMAT(P.REG_DT, '%Y-%m-%d %H:%i') AS REG_DT FROM TB_PRODUCT P INNER JOIN TB_CATEGORY C ON P.SERVICE_CD = C.SERVICE_CD WHERE 1=1 AND P.DEL_YN = 'N' AND C.DEL_YN = 'N' AND C.USE_YN = 'Y'
AND P.DISPLAY_YN = #{displayYn}
AND P.SALE_YN = #{saleYn}
AND P.SERVICE_CD = #{serviceCd}
ORDER BY C.SERVICE_ORDER ASC, P.DISPLAY_ORDER ASC, P.PRODUCT_NO DESC
